mapper.insert:
INSERT INTO board_file(
	board_no
	, board_ori_file_name
	, board_save_file_name
	, board_filetype
	, board_path
	, createdate
	, updatedate
) VALUES (
	#{boardNo}
	, #{boardOriFileName}
	, #{boardSaveFileName}
	, #{boardFileType}
	, #{boardPath}
	, NOW()
	, NOW()
)

mapper.delete:
DELETE FROM
	board_file
WHERE
	board_no = #{boardNo}
	AND board_file_no = #{boardFileNo}
