mapper.insert:

	SELECT last_insert_id()

INSERT INTO board(
 	emp_no
	, board_category
	, board_title
	, board_content
	, top_exposure
	, createdate
	, updatedate
) VALUES (
	#{empNo}
	, #{boardCategory}
	, #{boardTitle}
	, #{boardContent}
	, #{topExposure}
	, NOW()
	, NOW()
)

mapper.delete:
DELETE FROM board
WHERE
	emp_no = #{empNo}
AND
	board_no = #{boardNo}

mapper.update:
UPDATE
	board
SET
	board_category = #{boardCategory}
	, board_title = #{boardTitle}
	, board_content = #{boardContent}
	, top_exposure = #{topExposure}
WHERE
	emp_no = #{empNo}
AND
	board_no = #{boardNo}
