mapper.insert:
    INSERT INTO emp_no_list (
	    emp_no
	    , active
	    , createdate 
	) VALUES (
	    #{empNo}
	    , 'Y'
	    , NOW()
    )

INSERT INTO emp_info(
    emp_no
    , emp_name
    , dept_name
    , team_name
    , emp_position
    , access_level
    , emp_state
    , employ_date
    , retirement_date
    , createdate
    , updatedate
) VALUES (
    #{empNo}
    , #{empName}
    , #{deptName}
    , #{teamName}
    , #{empPosition}
    , #{accessLevel}
    , #{empState}
    , #{employDate}
    , '0000-00-00'
    , NOW()
    , NOW()
)

mapper.select:
SELECT COUNT(*) totalCount
FROM emp_info


        AND employ_date BETWEEN #{startDate} AND #{endDate}


        AND emp_state = #{empState}


        AND dept_name = #{deptName}


        AND team_name = #{teamName}


        AND emp_position = #{empPosition}


        AND (${searchCol} LIKE CONCAT('%', #{searchWord}, '%'))

SELECT emp_no empNo , emp_name empName , dept_name deptName , team_name teamName , emp_position empPosition , access_level accessLevel , emp_state empState , employ_date employDate , createdate , updatedate FROM emp_info
AND employ_date BETWEEN #{startDate} AND #{endDate}
AND emp_state = #{empState}
AND dept_name = #{deptName}
AND team_name = #{teamName}
AND emp_position = #{empPosition}
AND (${searchCol} LIKE CONCAT('%', #{searchWord}, '%'))
ORDER BY ${empDate} ASC
ORDER BY ${empDate} DESC
ORDER BY employ_date DESC
LIMIT #{beginRow}, #{rowPerPage}
