mapper.insert:

SELECT last_insert_id()

INSERT INTO reservation(
	reservation_no
,	emp_no
,	utility_no
,	reservation_date
,	reservation_time
,	createdate
) VALUES(
	#{reservationNo}
,	#{empNo}
,	#{utilityNo}
,	#{reservationDate}
,	#{reservationTime}
,	NOW()
)

mapper.select:
SELECT COUNT(*) 
FROM reservation
WHERE reservation_date = #{reservationDate} 
	AND utility_no = #{utilityNo} 
	AND emp_no = #{empNo}

SELECT COUNT(*) 
FROM reservation
WHERE reservation_date = #{reservationDate} 
	AND reservation_time = #{reservationTime}
	AND utility_no = #{utilityNo} 
	AND emp_no = #{empNo}

SELECT r.reservation_no reservationNo, r.emp_no empNo, r.utility_no utilityNo, r.reservation_date reservationDate, r.reservation_time reservationTime, r.createdate createdate, e.emp_name empName, u.utility_category utilityCategory FROM reservation r JOIN emp_info e ON r.emp_no = e.emp_no JOIN utility u ON r.utility_no = u.utility_no
AND DATE(r.reservation_date) >= #{startDate}
AND DATE(r.reservation_date) <= #{endDate}
AND e.emp_name LIKE CONCAT('%', #{searchWord}, '%')
AND u.utility_category LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY ${col} ASC
ORDER BY ${col} DESC
ORDER BY r.createdate DESC
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) CNT FROM reservation r JOIN emp_info e ON r.emp_no = e.emp_no JOIN utility u ON r.utility_no = u.utility_no
AND DATE(r.reservation_date) >= #{startDate}
AND DATE(r.reservation_date) <= #{endDate}
AND e.emp_name LIKE CONCAT('%', #{searchWord}, '%')
AND u.utility_category LIKE CONCAT('%', #{searchWord}, '%')
