mapper.insert:

SELECT last_insert_id()

INSERT INTO utility(
	utility_no
,	utility_category
,	utility_name
,	utility_info
,	createdate
,	updatedate
) VALUES(
	#{utilityNo} 
,	#{utilityCategory}
,	#{utilityName}
,	#{utilityInfo}
,	NOW()
,	NOW()
)

mapper.update:
UPDATE utility
SET utility_category = #{utilityCategory}
,	utility_name = #{utilityName}
,	utility_info = #{utilityInfo}
,	updatedate = NOW()
WHERE utility_no = #{utilityNo}
